import React from 'react';
import {
    Dropdown,
    DropdownToggle,
    DropdownMenu,
} from 'reactstrap';

class ProfileIcon extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            dropdownOpen: false
        }
    }

    toggle = () =>{
        this.setState(preState=>({
            dropdownOpen: !preState.dropdownOpen
        }))
    }

    render(){
        return(
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                <DropdownToggle
                    tag="span"
                    data-toggle="dropdown"
                    aria-expanded={dropdownOpen}
                >
                    <div class="pa4 tc">
                        <img
                            src="http://tachyons.io/img/logo.jpg"
                            class="br-100 ba h3 w3 dib" alt="avatar" />
                    </div>
                </DropdownToggle>
                <DropdownMenu>
                    <div onClick={this.toggle}>Custom dropdown item 1</div>
                    <div onClick={this.toggle}>Custom dropdown item 2</div>
                    <div onClick={toggle}>Custom dropdown item 3</div>
                    <div onClick={toggle}>Custom dropdown item 4</div>
                </DropdownMenu>
            </Dropdown>

        )
    }
}

export default ProfileIcon;
