import React from 'react';
import {
    Dropdown,
    DropdownToggle,
    DropdownItem,
    DropdownMenu
} from 'reactstrap';

class ProfileIcon extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            dropdownOpen: false
        }
    }

    toggle = () =>{
        console.log(this.state.dropdownOpen)
        this.setState(preState=>({
            dropdownOpen: !preState.dropdownOpen
        }))
    }

    render(){
        return(
            <div className='pa4 tc'>
                {/* <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                    <DropdownToggle
                        tag="span"
                        data-toggle="dropdown"
                        aria-expanded={this.state.dropdownOpen}
                    >
                        <div className="pa4 tc">
                            <img
                                src="http://tachyons.io/img/logo.jpg"
                                className="br-100 ba h3 w3 dib" alt="avatar" />
                        </div>
                    </DropdownToggle>
                    <DropdownMenu>
                        <DropdownItem>View Profile</DropdownItem>
                        <DropdownItem>Sign Out</DropdownItem>
                    </DropdownMenu>
                </Dropdown> */}
            
               
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} drop='left'>
              <DropdownToggle
                tag="span"
                onClick={this.toggle}
                data-toggle="dropdown"
                aria-expanded={this.state.dropdownOpen}
              >
                <img
                  src="http://tachyons.io/img/logo.jpg"
                  className="br-100 h3 w3 dib" alt="avatar" />
              </DropdownToggle>
              {/* Old <DropdownMenu className='b--transparent shadow-5' style={{marginTop: '20px', backgroundColor: 'rgba(255, 255, 255, 0.5)'}} right> */}
              <DropdownMenu className='b--transparent shadow-5' style={{marginLeft: '-6rem', backgroundColor: 'rgba(255, 255, 255, 0.5)'}} >
                <DropdownItem onClick={() => this.props.toggleModal()}>View Profile</DropdownItem>
                <DropdownItem onClick={() => this.props.onRouteChange('signout')}>Sign Out</DropdownItem>
              </DropdownMenu>
            </Dropdown>

            </div>
        )
    }
}

export default ProfileIcon;
